mapper.select:
select * from student_comment_course
where id in ( select commentId from comment_tag where tagId = #{tagId}
and deleteStatus = 0 ) and deleteStatus = 0

select * from student_comment_course
where selectId in ( select id from student_select_course where courseId = #{courseId}
and deleteStatus = 0 ) and deleteStatus = 0

select count(*) from student_comment_course
where selectId in ( select id from student_select_course where courseId = #{courseId} 
and deleteStatus = 0 ) and deleteStatus = 0
